ruleChapterList:
@js:
var list = new Array();
var a = result.replace("<!--ch-->","").trim().replace(/\s\s/g,"|").split(/\|/);
for (var i=0; i<a.length; i++) {
  var b = a[i].trim().split(/\s/);
  list.push({chapter_id:b[0], chapter_name:b[1]});
}
list

ruleSearchList:
@js:
var list = new Array();
var a = result.trim().replace(/\|*$/g,"").split(/\|/);
for (var i=0; i<a.length; i++) {
  var b = a[i].trim().replace(/<font.*?>(.*?)<\/font>(.*?)/,"$1$2").split(",");
  list.push({id:b[0], type:[b[2],b[3]], name:b[4], last:b[6], author_name:b[8]});
}
list
